# ============================================================================
# UTILITY FUNCTIONS
# ============================================================================

rescale_Her <- function(runs, dat) {
  # Rescale Ftarget and Btrigger in `runs` to [0,1] using ranges from `dat`.
  ret <- runs
  minF <- min(dat$Ftarget)
  rangeF <- diff(range(dat$Ftarget))
  ret$Ftarget <- (ret$Ftarget - minF) / rangeF
  
  minB <- min(dat$Btrigger)
  rangeB <- diff(range(dat$Btrigger))
  ret$Btrigger <- (ret$Btrigger - minB) / rangeB
  
  return(ret)
}

unrescale_Her <- function(runs, dat) {
  # Undo the rescaling performed by `rescale_Her` to recover original units.
  ret <- runs
  minF <- min(dat$Ftarget)
  rangeF <- diff(range(dat$Ftarget))
  ret$Ftarget <- ret$Ftarget * rangeF + minF
  
  minB <- min(dat$Btrigger)
  rangeB <- diff(range(dat$Btrigger))
  ret$Btrigger <- ret$Btrigger * rangeB + minB
  
  return(ret)
}

equal_tol <- function(x, y, tol = 1e-12) {
  # Numeric equality check with tolerance
  abs(x - y) < tol
}

# Helper function to calculate prediction bounds
get_bounds <- function(mu, sd, is_log = FALSE) {
  # Calculate 5%, 50%, 95%, and 99.9% quantiles
  bounds <- cbind(
    lower_90 = mu + qnorm(0.05, 0, sd),
    mean = mu,
    upper_90 = mu + qnorm(0.95, 0, sd),
    upper_999 = mu + qnorm(0.999, 0, sd)
  )
  
  # If predictions were on log scale, transform back
  if (is_log) {
    bounds <- exp(bounds)
  }
  
  return(bounds)
}

# Create GP slice function - FIXED VERSION
create_gp_slice <- function(gp_model, fixed_col, fixed_value, ref_data) {
  # Filter data where the fixed column equals the fixed value (with tolerance)
  slice_df <- ref_data[abs(ref_data[[fixed_col]] - fixed_value) < 1e-9, ]
  
  if (nrow(slice_df) == 0) {
    stop("No data found for that fixed value. Check your value.")
  }
  
  # Return the slice data - this will be used for prediction
  return(slice_df)
}

