     xlab = "Ftarget", ylab = "Catch (log scale)",
     main = "Catch vs F target")

plot(dat$Btrigger, dat$catch_median_long, log = "y",
     xlab = "Btrigger", ylab = "Catch (log scale)",
     main = "Catch vs B trigger")

# Plot risk vs parameters
plot(dat$Ftrgt, dat$risk1_full, log = "y",
     xlab = "Ftarget", ylab = "Risk (log scale)",
     main = "Risk vs F target")
abline(h = 0.05, col = "red", lwd = 2)

plot(dat$Btrigger, dat$risk1_full, log = "y",
     xlab = "Btrigger", ylab = "Risk (log scale)",
     main = "Risk vs B trigger")
abline(h = 0.05, col = "red", lwd = 2)

# Check dimensions
cat("Number of unique Ftarget values:", length(unique(dat$Ftrgt)), "\n")
cat("Number of unique Btrigger values:", length(unique(dat$Btrigger)), "\n")

# ============================================================================
# ROUND 1: INITIAL SAMPLING AND GP FITTING
# ============================================================================

set.seed(18)

# Select initial design points using space-filling
round1 <- data.frame(
  Ftarget = sample(unique(dat$Ftrgt)[floor(seq(2, 40, length = 8))]),
  Btrigger = sample(unique(dat$Btrigger), size = 8)
)

plot(round1$Btrigger, round1$Ftarget, 
     xlab = "Btrigger", ylab = "Ftarget",
     main = "Round 1 Design Points", pch = 16, cex = 1.5)

# Create full grid for predictions
Ftarget <- sort(unique(dat$Ftrgt))
Btrigger <- sort(unique(dat$Btrigger))
dat1 <- data.frame(expand.grid(Ftarget = Ftarget, Btrigger = Btrigger))

# Prepare rescaled grid
gridd <- dat1[, c("Ftarget", "Btrigger")]
gridd <- rescale_Her(gridd[order(gridd$Ftarget, gridd$Btrigger), ], dat = dat1)

# Get responses for Round 1 design points - FIXED column name matching
dat_run <- left_join(round1, dat, by = c("Ftarget" = "Ftrgt", "Btrigger" = "Btrigger"))
names(dat_run) <- c("Ftarget", "Btrigger", "C_long", "risk_long")

# Rescale Round 1 runs
runs <- rescale_Her(dat_run, dat = dat1)

# ============================================================================
# FIT GAUSSIAN PROCESS EMULATORS
# ============================================================================

# Build emulator for log(catch)
res_cat <- log(runs$C_long)
gp_cat <- km(
  ~ .^2, 
  design = runs[, c("Ftarget", "Btrigger")],
  estim.method = "MLE",
  response = res_cat,
  nugget = 1e-12 * var(res_cat),
  covtype = "exp"
)

# Build emulator for log(risk)
res_risk <- log(runs$risk_long)
gp_risk <- km(
  ~ .^2,
  design = runs[, c("Ftarget", "Btrigger")],
  estim.method = "MLE",
  response = res_risk,
  nugget = 1e-12 * var(res_risk),
  covtype = "exp"
)

# ============================================================================
# MAKE PREDICTIONS ON FULL GRID
# ============================================================================

pred_risk1_g <- predict(gp_risk, newdata = gridd, type = "SK")
pred_cat1_g <- predict(gp_cat, newdata = gridd, type = "SK")

# Transform predictions back from log scale
med_risk1 <- exp(pred_risk1_g$mean)
med_cat1 <- exp(pred_cat1_g$mean)

# Probability that risk <= 0.05
prisk1 <- pnorm(log(0.05), pred_risk1_g$mean, pred_risk1_g$sd + 1e-12)


# ============================================================================
# IDENTIFY PROMISING REGIONS
# ============================================================================

# Best catch observed so far where risk is below 0.05
best_catch_so_far <- max(runs$C_long[runs$risk_long < 0.05])

# Probability that catch exceeds best so far
pcat1 <- pnorm(log(best_catch_so_far), pred_cat1_g$mean, pred_cat1_g$sd + 1e-12)

# Mark promising points
eps <- 1e-4
possible1 <- (apply(cbind((1 - pcat1), prisk1), 1, min) > eps)

# Visualizations
image2D(
  matrix(med_cat1, nrow = 11),
  y = sort(unique(dat$Ftrgt)),
  x = sort(unique(dat$Btrigger)),
  xlab = "Btrigger",
  ylab = "Ftarget",
  main = "Predicted Median Catch"
)

image2D(
  matrix(1 - pcat1, nrow = 11),
  y = sort(unique(dat$Ftrgt)),
  x = sort(unique(dat$Btrigger)),
  xlab = "Btrigger",
  ylab = "Ftarget",
  breaks = c(-1e-12, 0.0001, 0.05, 0.5, 0.9, 1),
  main = "P(Catch > Best So Far)"
)

# Region with good risk AND better catch
image2D(
  matrix(possible1 * (1 - pcat1), nrow = 11),
  y = sort(unique(dat$Ftrgt)),
  x = sort(unique(dat$Btrigger)),
  xlab = "Btrigger",
  ylab = "Ftarget",
  breaks = c(-1e-12, 0.0001, 0.05, 0.5, 0.9, 1),
  main = "Promising Region (Safe & Better Catch)"
)

# ============================================================================
# PREPARE FOR NEXT ROUND
# ============================================================================

# Collect potential points for next iteration
pot_points <- gridd[possible1, ]

# Best safe point found so far
best_so_far <- runs[runs$risk_long < 0.05, ][
  which.max(runs$C_long[runs$risk_long < 0.05]), 
  c("Ftarget", "Btrigger")
]

# Remove already evaluated points
pot_points <- setdiff(pot_points, runs[, 1:2])

cat("\nBest safe point so far:\n")
print(unrescale_Her(best_so_far, dat = dat1))
cat("\nBest safe catch:", best_catch_so_far, "\n")
cat("Number of promising unevaluated points:", nrow(pot_points), "\n")

# ============================================================================
# CREATE 1D SLICES FOR DETAILED VISUALIZATION
# ============================================================================

# Create slices through the best point found so far

# Slice 1: Vary Ftarget, fix Btrigger at best value
slice_vary_F <- create_gp_slice(
  gp_model = gp_cat,
  fixed_col = "Btrigger",
  fixed_value = best_so_far$Btrigger,
  ref_data = gridd
)

# Slice 2: Vary Btrigger, fix Ftarget at best value
slice_vary_B <- create_gp_slice(
  gp_model = gp_cat,
  fixed_col = "Ftarget",
  fixed_value = best_so_far$Ftarget,
  ref_data = gridd
)

# Get indices for these slices in the full grid
mask_vary_F <- abs(gridd$Btrigger - best_so_far$Btrigger) < 1e-9
mask_vary_B <- abs(gridd$Ftarget - best_so_far$Ftarget) < 1e-9

# Calculate prediction bounds for catch (logged response)
qs_cat_F <- get_bounds(
  pred_cat1_g$mean[mask_vary_F], 
  pred_cat1_g$sd[mask_vary_F], 
  is_log = TRUE
)

qs_cat_B <- get_bounds(
  pred_cat1_g$mean[mask_vary_B], 
  pred_cat1_g$sd[mask_vary_B], 
  is_log = TRUE
)

# Calculate prediction bounds for risk (logged response)
qs_risk_F <- get_bounds(
  pred_risk1_g$mean[mask_vary_F], 
  pred_risk1_g$sd[mask_vary_F], 
  is_log = TRUE
)

qs_risk_B <- get_bounds(
  pred_risk1_g$mean[mask_vary_B], 
  pred_risk1_g$sd[mask_vary_B], 
  is_log = TRUE
)

# Get x-axis values for slices (unscaled for plotting)
x_vals_F <- sort(unique(gridd$Ftarget))  # Varying F
x_vals_B <- sort(unique(gridd$Btrigger)) # Varying B

# Unscale for interpretable plots
x_vals_F_orig <- x_vals_F * diff(range(dat1$Ftarget)) + min(dat1$Ftarget)
x_vals_B_orig <- x_vals_B * diff(range(dat1$Btrigger)) + min(dat1$Btrigger)

# ============================================================================
# PLOTTING SECTION - COMPREHENSIVE VISUALIZATION
# ============================================================================

# Save high-quality EPS file
setEPS()
cairo_ps("case_study8_round1_exponential.eps", width = 10, height = 10)

par(mfrow = c(2, 2))
par(oma = c(2, 2, 1, 1))
par(mar = c(4, 4, 2, 1))

cex_arg <- 1.3

# PLOT 1: Catch slice varying Ftarget
plot(
  x_vals_F_orig, qs_cat_F[, "mean"],
  type = "n",
  ylim = range(c(qs_cat_F, runs$C_long)),
  xlab = "Ftarget",
  ylab = "Catch",
  main = "Catch: Varying Ftarget"
)

# Add observed points
points(runs$Ftarget * diff(range(dat1$Ftarget)) + min(dat1$Ftarget), 
       runs$C_long, pch = 16, cex = cex_arg)

# Add uncertainty band and mean using SpenceTools
SpenceTools::uncertain_plot(x_vals_F_orig, t(qs_cat_F[, c("lower_90", "mean", "upper_90")]), add = TRUE)


# PLOT 2: Catch slice varying Btrigger
plot(
  x_vals_B_orig, qs_cat_B[, "mean"],
  type = "n",
  ylim = range(c(qs_cat_B, runs$C_long)),
  xlab = "Btrigger",
  ylab = "Catch",
  main = "Catch: Varying Btrigger"
)

points(runs$Btrigger * diff(range(dat1$Btrigger)) + min(dat1$Btrigger),
       runs$C_long, pch = 16, cex = cex_arg)

SpenceTools::uncertain_plot(x_vals_B_orig, t(qs_cat_B[, c("lower_90", "mean", "upper_90")]), add = TRUE)


# PLOT 3: Risk slice varying Ftarget
plot(
  x_vals_F_orig, qs_risk_F[, "mean"],
  type = "n",
  ylim = range(c(qs_risk_F, qs_risk_B, runs$risk_long, 0.05)),
  xlab = "Ftarget",
  ylab = "Risk",
  main = "Risk: Varying Ftarget",
)

points(runs$Ftarget * diff(range(dat1$Ftarget)) + min(dat1$Ftarget),
       runs$risk_long, pch = 16, cex = cex_arg)

SpenceTools::uncertain_plot(x_vals_F_orig, t(qs_risk_F[, c("lower_90", "mean", "upper_90")]), add = TRUE)

# PLOT 4: Risk slice varying Btrigger
plot(
  x_vals_B_orig, qs_risk_B[, "mean"],
  type = "n",
  ylim = range(c(qs_risk_F, qs_risk_B, runs$risk_long, 0.05)),
  xlab = "Btrigger",
  ylab = "Risk",
  main = "Risk: Varying Btrigger",
)

# Plot ALL observed points (all 8 design points)
points(runs$Btrigger * diff(range(dat1$Btrigger)) + min(dat1$Btrigger),
       runs$risk_long, pch = 16, cex = cex_arg)

SpenceTools::uncertain_plot(x_vals_B_orig, t(qs_risk_B[, c("lower_90", "mean", "upper_90")]), add = TRUE)

dev.off()